    def render_leaderboard(self, players: List[Dict[str, Any]], classes: Dict[str, str],
                          search_term: str = "", challenge_info: Optional[Dict[str, Any]] = None,
                          mob_cooldowns: Optional[Dict[str, float]] = None,
