            """

        # Leaderboard table
        content += '<div class="card">'
        content += '<table class="table">'
        content += '''
        <thead>
            <tr>
                <th>Rank</th>
                <th>Player</th>
                <th>Class</th>
                <th>Level</th>
                <th>XP</th>
