                el.classList.remove('alert-error', 'alert-success');
                if (kind === 'error') {
                    el.classList.add('alert-error');
                } else if (kind === 'success') {
                    el.classList.add('alert-success');
                }
            }

            // Interactive features removed - this is now a read-only interface
        })();
        </script>
        """

        return content

